import { strict as assert } from 'assert'

import { Stock42 } from '../src/index'

async function init() {
	try {
		const { track, screenshot } = await Stock42({
			appVersion: '1.0',
			appName: 'Stock42Test',
			userAgent: 'stock42agent',
			applicationToken: 'bd23a6fb-02e6-4ac6-8e5c-2aa28bd83db2-FV2AH3',
			deviceId: '1',
		})

		assert.ok(track, 'track function should be defined')
		assert.ok(screenshot, 'screenshot function should be defined')

		for (let x = 0; x < 11; x++) {
			setTimeout(() => {
				track({
					eventName: 'custom',
					screenName: 'home',
					functionName: `function${x}`,
					data: {
						x,
						msg: 'random number',
					},
				})
			}, 200 * x)
		}

		screenshot({
			screenName: 'screentesting',
			base64:
				'data:image/png;base64,',
		})
	} catch (err) {
		process.exit(1)
	}
}

init()
